INSERT INTO `block` (`block_id`, `block_type_id`, `page_id`, `size`, `classes`, `style`, `link`, `param1`, `param2`, `param3`, `weight`) VALUES
(5,	2,	6,	'2x1',	'back_2x1_c',	NULL,	'',	'<h1>Kdo za tím stojí?</h1>\n<p>Science slam pořádá Masarykova univerzita jako nový formát popularizační akce, při němž se výzkumníci mohou potkat se širokou veřejností laickou i odbornou. S nápadem přišla Jana Posltová, již inspiroval Bernhard Weingartner ze <a class=\"link\" href=\"http://www.scienceslam.at/about/\">Science Slam Vienna</a>, a zaujala jím postupně všechny stávající organizátory, kteří svými inovativními nápady vytvářejí celkový koncept, obsah i formu akce. S&nbsp;přípravou v různých rolích pomáhají ještě další kamarádi a dobří lidé, časem zde budou jejich jména uvedena.</p>\n\n<p>Vaše nápady a připomínky nám pište na <a href=\"mailto:scienceslam@muni.cz\" class=\"link\">scienceslam@muni.cz</a>.</p>',	NULL,	NULL,	0),
(7,	3,	6,	'1x1',	'',	'images/blocks/posltova.jpg',	'',	'Jana Posltová',	'vedení týmu, zdroj entuziasmu',	NULL,	0),
